import java.util.Scanner;
public class Main2 {

	public static void main(String[] args) {
		Scanner input;
		input=new Scanner(System.in);
		
		// TODO Auto-generated method stub
		while(input.hasNext()) {
			int a=input.nextInt();
			int b=input.nextInt();
			int sum=a+b;
			System.out.println(sum);
		}

	}

}
